<script>
    document.addEventListener('DOMContentLoaded', function () {
        const statusSelect = document.getElementById('status');
        const notificationContainer = document.getElementById('notification-method-container');
        const emailOption = document.querySelector('#notification_method option[value="email"]');

        // Show/hide the notification method dropdown based on the status
        statusSelect.addEventListener('change', function () {
            if (this.value === 'done') {
                notificationContainer.style.display = 'block';
            } else {
                notificationContainer.style.display = 'none';
            }
        });

        // Trigger change event on page load to ensure correct visibility
        statusSelect.dispatchEvent(new Event('change'));
    });
</script>
